
package Task3;

public class controlFlow_using_loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=10;
		int sum=0;
System.out.println("sum of the first 10  numbers");
	for(int i=1;i<=n;i++)
		sum+=i;
	System.out.println(" the sum of the first 10  numbers is:"+sum);
	
	}

}
